import React, {useState} from 'react';
import{BiMenuAltRight} from "react-icons/bi";
// import{AiOutlineClose} from "react-icons/ai";

const Header = () => {
    // const toggle = () => {
    //     return document.getElementById("button-test").classList.toggle("isMenu")
    // }
    const [isActive, setIsActive] = useState(false);

    const toggle = event => {
      // 👇️ toggle isActive state on click
      setIsActive(current => !current);
    };
    return (
<header className='header'>
    <div className='header__content'>
        <h2 className='header__content__logo'>lalouX.</h2>
        <nav className='header__content__nav' id="button-test">

            <ul>
                <li>
                    <a href="/">Page one</a>
                </li>
                <li>
                    <a href="/">Page two</a>
                </li>
                <li>
                    <a href="/">Page three</a>
                </li>
            </ul>
            <button>CTA PAGE</button>
        </nav>
        <div onClick={() => toggle()} className='header__content__toggle'>
        <BiMenuAltRight />
        </div>
    </div>
</header>
    );
};
export default Header;
